import React, { Component } from 'react';
import CopyrightUpdate from 'copyright-update';
import '../css/Subscribe.css';
import logoWordmark from './../img/universe-wordmark.svg';

// Using a ES6 class component as it contains state.
class SubscribeSuccess extends Component {
  render() {
    return (
      <div className="container-fluid pl-0 pr-0">
        <div 
          className="
            container-fluid 
            d-flex 
            align-items-center 
            justify-content-center
            pl-0 pr-0
          ">
          <div className="card col-md-8">
            <div className="card-body">
              <div className="text-center mb-4">
                <a href="/" className="brand">
                  <img
                    className="brand-img"
                    src={logoWordmark} 
                    alt="Papriika Logo"
                  />
                </a>
                <p className="lead-md font-weight-bold">
                  Almost finished...
                </p>
                <p>
                  We need to confirm your email address.
                </p>
                <p className="mb-4b">
                  To complete the subscription process, please click the link in the email we just sent you.
                </p>
                <a
                  className="btn btn-secondary btn-subscribe mx-auto"
                  style={{
                    textDecoration: 'none'
                  }}
                  href="/" 
                  role="button"
                  tabIndex="1">
                  Done
                </a>
              </div>
            </div>
            <div className="text-center small letter-space mt-9 mb-4">
              <CopyrightUpdate 
                style={{
                  display: 'inline-block'
                }}
              />
              &nbsp;Universe Labs Inc.
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default SubscribeSuccess;
